import os
from parallel_ai import ParallelClient

# --- Configuration ---
# It's best practice to set your API key as an environment variable
client = ParallelClient(api_key=os.environ.get("PARALLEL_AI_API_KEY"))
TARGET_COMPANY_NAME = "Microsoft Corp" # <-- Change this for each company

# --- This is the core "Prompt" ---
# It's a structured Task Specification telling the AI exactly what to do.
task_spec = {
    "task": f"Extract detailed executive and contact information for the company: {TARGET_COMPANY_NAME}",
    "output_schema": {
        "type": "object",
        "properties": {
            "company_name": {"type": "string", "description": "The official name of the company."},
            "stock_ticker": {"type": "string", "description": "The primary stock ticker symbol."},
            "official_website": {"type": "string", "description": "The main corporate website URL."},
            "leadership_page_url": {
                "type": "string",
                "description": "The direct URL to the 'About Us', 'Leadership', or 'Management Team' page."
            },
            "email_format_pattern": {
                "type": "string",
                "description": "The most likely corporate email format, inferred from public data (e.g., '{first}.{last}@company.com', '{f}{last}@company.com')."
            },
            "top_executives": {
                "type": "array",
                "description": "A list of the top 5 C-suite level executives.",
                "items": {
                    "type": "object",
                    "properties": {
                        "executive_name": {"type": "string"},
                        "executive_title": {"type": "string", "description": "The official title, e.g., 'Chief Executive Officer'."},
                        "source_snippet": {
                            "type": "string",
                            "description": "The exact text snippet from the leadership page that confirms their name and title."
                        }
                    },
                    "required": ["executive_name", "executive_title", "source_snippet"]
                }
            }
        },
        "required": ["company_name", "leadership_page_url", "email_format_pattern", "top_executives"]
    }
}

# --- Execute the Task ---
try:
    task = client.tasks.create(task_spec)
    print("✅ Task submitted successfully!")
    print(f"   Task ID: {task.id}")
    print("Waiting for results...")

    # Wait for the task to complete and get the results
    result = task.get_result()

    # --- Display the Structured Output ---
    import json
    print("\n--- Results ---")
    print(json.dumps(result, indent=2))

except Exception as e:
    print(f"❌ An error occurred: {e}")

